def insert_collection_to_DB():
    conn=createConnection()
    c=conn.cursor()
    c.execute("SELECT COUNT(*) FROM collections")
    if not c.fetchone()[0]:
        # insert collection names only if the table is empty (i.e first time running the program)
        for collection_name, word_list in word_collection_names.items():
            for word in word_list:
                c.execute("INSERT INTO collections (word, collection) VALUES (?, ?)", (word, collection_name))
    conn.commit()

